mapper.insert:

INSERT INTO
ARTIST
VALUES 
(
(SELECT NVL(MAX(artist_no), 0) + 1 FROM artist)
, #{artist_name}
, #{artist_nation}
, #{artist_company}
, #{artist_type}
, #{artist_group}
, #{artist_member}
, #{artist_profile}
, #{artist_info1}
, #{artist_info2}
)

INSERT INTO
ALBUM
VALUES
(
(SELECT NVL(MAX(a_no), 0) + 1 FROM album)
, #{a_name}
, #{a_cover}
, #{a_date}
, #{a_publishing}
, #{a_agency}
, #{a_introduce1}
, #{a_introduce2}
, (SELECT artist_no FROM artist WHERE artist_name = #{artist_name})
)
