mapper.select:
SELECT *
FROM member
WHERE m_id = #{m_id} AND m_password = #{m_password}

SELECT count(*)
FROM member
WHERE m_id = #{m_id}

SELECT count(*)
FROM member
WHERE m_nickname = #{m_nickname}

SELECT count(*)
FROM member
WHERE m_email = #{m_email}

SELECT count(*)
FROM member
WHERE m_phone = #{m_phone}

SELECT *
FROM member
WHERE m_birth = #{m_birth} AND m_email = #{m_email} AND m_phone = #{m_phone}

SELECT *
FROM member
WHERE m_id = #{m_id} AND m_birth = #{m_birth} AND m_email = #{m_email} AND m_phone = #{m_phone}

mapper.update:
UPDATE member
SET m_password = #{m_password}
WHERE m_id = #{m_id}
