mapper.select:
select count(*) from qna where
1=1
m_id=#{m_id}

select t2.* from (select rownum rnum, t1.* from (select qna_no, qna_title, qna_content , m_id, qna_date, origin_no , gr_ord, gr_layer from QNA where
1=1
m_id=#{m_id}
ORDER BY origin_No DESC, gr_Ord ASC)t1
where rownum <= #{cri.pageNum} * #{cri.amount})t2 where rnum > (#{cri.pageNum} -1) * #{cri.amount}

mapper.update:
update qna set 
gr_ord = gr_ord + 1 
where origin_no = #{origin_no} 
	and gr_ord > #{gr_ord}
